# coding=utf-8
"""The goal of this module is to automate my hattrick player status monitoring
"""
import re
import requests

from getpass import getpass


class Hattrick(object):
    """Represent a Hattrick login sesion"""

    MAIN_PAGE = "https://www.hattrick.org/en/"

    LOGIN_PAGE = "https://www.hattrick.org/en/Startpage3.aspx"

    HEADER = {"User-Agent":"Mozilla/5.0 (X11; Linux x86_64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/51.0.2704.103 Safari/537.36"}

    FORM_DATA = {
        "ctl00_sm_HiddenField": ";;System.Web.Extensions, Version=4.0.0.0, Culture=neutral, PublicKeyToken=31bf3856ad364e35:en-GB:ad6c4949-7f20-401f-a40f-4d4c52722104:ea597d4b:b25378d2",
        "__EVENTTARGET": "ctl00$CPContent$ucLogin$butLogin",
        "__VIEWSTATE": "",
        "__VIEWSTATEGENERATOR": "98A44CFC",
        "__EVENTVALIDATION": "",
        "ctl00$CPHeader$ucMenu$ucLanguages$ddlLanguages": "2",
        "loginname": "{{signup.loginname}}",
        "password": "{{signup.password}}",
        "ctl00$CPContent$ucLogin$txtUserName": "stevensson",
    }

    PASSWORD_FIELD = "ctl00$CPContent$ucLogin$txtPassword"

    def __init__(self):
        """Initialise a new session before login"""
        self.session = None
        self.server_url = None
        self.server_id = None
        self.team_id = None

    def __enter__(self):
        """Login to Hattrick
        If any step fail, an exception is raised so if we managed to enter, we are in.
        """
        self.session = requests.Session()

        response = self.session.get(self.MAIN_PAGE)
        response.raise_for_status()

        self.FORM_DATA[self.PASSWORD_FIELD] = getpass()

        response = self.session.post(self.LOGIN_PAGE, data=self.FORM_DATA, headers=self.HEADER)
        response.raise_for_status()

        if match := re.search(r"^(?P<server_url>.*www(?P<server_id>\d+)\.hattrick\.org)",
                              response.url):
            self.server_url = match.group("server_url")
            self.server_id = int(match.group("server_id"))

            if match := re.search(r"currentTeamId=(?P<team_id>\d+)",
                                  response.request.headers["Cookie"]):
                self.team_id = int(match.group("team_id"))
            else:
                raise RuntimeError("Unexpected Cookie: '{}'"
                                   .format(response.request.headers["Cookie"]))
        else:
            raise RuntimeError("Unexpected URL: '{}'".format(response.url))

        print("We're in! :)")

        return self

    def __exit__(self, exc_type, exc_val, exc_tb):
        """Logout and exit the session too"""
        response = self.session.get("{}/{}".format(self.server_url, "/?action=logout"),
                                    headers=self.HEADER)
        response.raise_for_status()

        print("We're out! :)")

        self.session.__exit__()


with Hattrick() as ht:
    pass

